/* Global Variables */
:root:root {
  /* for AccentSwap
  --accent-h: 248;
  --accent-s: 45%;
  --accent-l: 66%;
  */
  --customEmojiIconURL: url(https://cdn.discordapp.com/emojis/1217110775601172591.gif?size=240&quality=lossless);
  --customEmojiIconMask: url(https://cdn.discordapp.com/emojis/1217110775601172591.gif?size=240&quality=lossless);
  --prompt-text: "be silly :3";
  --highlight-select: linear-gradient(-80deg, #50d7e940 20%, #ffa60040);
  --highlight-icon: #ffd000;
  /* do you want to see your quests/billing/nitro settings? */
  /* yes, I want to see it -> --billing: block !important; */
  /* no, hide it -> --billing: none; */
  --billing: block !important;
  /* do you want to see server boosting/server shop upsell? */
  /* yes, I want to see it -> --boosting: block !important; */
  /* no, hide it -> --boosting: none; */
  --boosting: none;



      /* theme accent colors */
    --accent-1: hsl(347, 50%, 65%); /* links */
    --accent-2: hsl(347, 50%, 60%); /* general unread/mention elements */
    --accent-3: hsl(347, 50%, 55%); /* accent buttons */
    --accent-4: hsl(347, 50%, 50%); /* accent buttons when hovered */
    --accent-5: hsl(347, 50%, 45%); /* accent buttons when clicked */
    --mention: hsla(347, 50%, 45%, 0.1); /* mentions & mention messages */
    --mention-hover: hsla(347, 50%, 45%, 0.05); /* mentions & mention messages when hovered */

}

/* Viggy Loader */
[class^=content]:has(> video[data-testid="app-spinner"]) {
  position: relative;

  &::after {
    content: '';
    position: absolute;
    top: 0px;
    left: 0px;
    translate: 25%;
    width: 200px;
    height: 180px;
    background-image: url('https://media.discordapp.net/stickers/1217112512374505613.png?size=320') !important;
    background-size: contain;
    background-repeat: no-repeat;
    background-position: center;
  }

  video {
    visibility: hidden;
  }
}


/* settings modal */
.standardSidebarView_c25c6d {
  top: 0 !important;
}

.layer_d4b6c5+.layer_d4b6c5 {
  border-radius: 16px !important;
  box-shadow: 5px 5px 25px rgb(0, 0, 0);
  border: 2px solid var(--border-strong);
  width: 1130px !important;
