/* the cover art */
#vc-spotify-info-wrapper>img {
  display: none;
}

#vc-spotify-player:not(:has(#vc-spotify-album-expanded-wrapper)):hover #vc-spotify-album-image {
  display: none;
}

/* the song info */
#vc-spotify-player #vc-spotify-titles {
  margin-left: 10px;
}

#vc-spotify-player #vc-spotify-titles .size14_e8b2ab {
  font-size: 14px;
}

#vc-spotify-song-title::before,
#vc-spotify-song-title+div::before,
#vc-spotify-song-title+div+div::before {
  background-color: var(--interactive-active);
}

.vc-spotify-artist,
.vc-spotify-album {
  color: var(--interactive-active);
}

/* the bar, its selector and the time */
#vc-spotify-player .bar_c7a159 {
  background-color: hsl(var(--white-500-hsl) / 0.1);
}

#vc-spotify-player .barFill_c7a159 {
  background-image: var(--vc-spotify-track-image);
  background-size: 240px;
  background-position-y: -127px;
  background-position-x: -7px;
  filter: blur(2px) brightness(100%);
}

#vc-spotify-progress-bar>[class^="slider"] [class^="grabber"] {
  background-color: var(--interactive-active);
  border-color: var(--interactive-active);
  width: 1px !important;
  height: 4px !important;
  margin-top: 7px;
  opacity: 1 !important;
}

.vc-spotify-progress-time {
  top: 16px;
}

#vc-spotify-progress-bar {
  margin: 4px 0 -10px;
}

/* the buttons */
.vc-spotify-button {
  color: hsl(var(--white-500-hsl) / 0.5);
}

.vc-spotify-button:hover {
  background: none;
  color: hsl(var(--white-500-hsl) / 1);
}

.vc-spotify-button.vc-spotify-shuffle-off,
.vc-spotify-button.vc-spotify-repeat-off {
  color: hsl(var(--white-500-hsl) / 0.3);
}

.vc-spotify-button.vc-spotify-shuffle-off:hover,
.vc-spotify-button.vc-spotify-repeat-off:hover {
  color: hsl(var(--white-500-hsl) / 1);
}

.vc-spotify-button.vc-spotify-shuffle-on,
.vc-spotify-button.vc-spotify-repeat-context,
.vc-spotify-button.vc-spotify-repeat-track {
  color: hsl(var(--white-500-hsl) / 0.7);
}

.vc-spotify-button.vc-spotify-shuffle-on:hover,
.vc-spotify-button.vc-spotify-repeat-context:hover,
.vc-spotify-button.vc-spotify-repeat-track:hover {
  color: hsl(var(--white-500-hsl) / 1);
}

/* cover art as background by vending.machine */
#vc-spotify-player {
  --blur-amount: 2px;
  /* higher px = stronger blur, 0 = no blur */
  --darken-percent: 0.5;
  /* 0 = album cover is not darkened, 1 = album cover is darkened fully (black) */
}

#vc-spotify-player {
  background: transparent !important;
  position: relative;
}

#vc-spotify-player>* {
  position: relative;
  z-index: 1;
}

#vc-spotify-player::after {
  content: "";
  position: absolute;
  display: block;
  height: 100%;
  width: 100%;
  top: 0;
  left: 0;
  background: linear-gradient(rgba(0, 0, 0, var(--darken-percent)), rgba(0, 0, 0, var(--darken-percent))), var(--vc-spotify-track-image);
  background-size: cover;
  background-position: center;
  filter: blur(var(--blur-amount));
  clip-path: inset(0);
}

#vc-spotify-player+[class^="container"] {
  background: transparent;
}

#vc-spotify-player+[class^="container"] {
  background-color: #ffffff00;
  backdrop-filter: blur(1px);
}

